import React from "react";
import "./Services.css";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  FaIdCard,
  FaPassport,
  FaVoteYea,
  FaBriefcase,
  FaAddressCard,
  FaFileAlt,
} from "react-icons/fa";

const servicesList = [
  {
    title: "PAN Card Apply / Update",
    desc: "New PAN, corrections and updates with full guidance.",
    icon: <FaIdCard />,
    color: "#0066ff",
    link: "/apply-pan-card",
  },
  {
    title: "Passport Services",
    desc: "New passport, renewal and corrections made easy.",
    icon: <FaPassport />,
    color: "#00a86b",
    link: "/passport-application",
  },
  {
    title: "Voter ID Services",
    desc: "New voter registration and detail updates.",
    icon: <FaVoteYea />,
    color: "#ff9800",
    link: "/voter-id",
  },
  {
    title: "Job Application Forms",
    desc: "Government & private job form assistance.",
    icon: <FaBriefcase />,
    color: "#8e44ad",
    link: "/job-application",
  },
  {
    title: "Aadhaar Services",
    desc: "Aadhaar update, correction & download support.",
    icon: <FaAddressCard />,
    color: "#e53935",
    link: "/aadhar-services",
  },
  {
    title: "Government Certificates",
    desc: "Income, caste, domicile & other certificates.",
    icon: <FaFileAlt />,
    color: "#2c3e50",
    link: "/government-certificates",
  },
];

const Services = () => {
  const navigate = useNavigate();

  return (
    <div className="services-page">

      {/* HEADER */}
      <motion.div
        className="services-header"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
      >
        <h1>Our Digital & CSC Services</h1>
        <p>
          Trusted government and digital services — fast, secure and guided by
          experts.
        </p>
      </motion.div>

      {/* SERVICES GRID */}
      <div className="services-grid">
        {servicesList.map((service, index) => (
          <motion.div
            key={index}
            className="service-card"
            style={{ "--accent": service.color }}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.12, duration: 0.5 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.04 }}
            onClick={() => navigate(service.link)}
          >
            <div className="service-icon">{service.icon}</div>

            <h3>{service.title}</h3>
            <p>{service.desc}</p>

            <span className="service-cta">
              Explore Service →
            </span>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Services;
