import React, { useState } from "react";
import { validateForm } from "./FormValidation";
import { submitPanCardForm } from "../api/formApi";
import "./FormStyles.css";

const PanCardForm = () => {
  const [fields, setFields] = useState({
    fullName: "",
    fatherName: "",
    dob: "",
    email: "",
    phone: "",
    address: "",
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  // Handle input fields
  const handleInput = (e) => {
    setFields({ ...fields, [e.target.name]: e.target.value });
  };

  // Submit Form
  const handleSubmit = async (e) => {
    e.preventDefault();

    const validation = validateForm(fields);

    if (Object.keys(validation).length !== 0) {
      setErrors(validation);
      return;
    }

    setErrors({});
    setLoading(true);

    console.log("ðŸ“¤ Sending PAN Card form to backend:", fields);

    const response = await submitPanCardForm(fields);

    console.log("ðŸ“¥ Backend Response:", response);

    setLoading(false);

    if (response.success) {
      setSubmitted(true);
      alert("PAN Card request submitted successfully!");
    } else {
      alert("Failed: " + response.message);
    }
  };

  return (
    <div className="form-container">
      <form className="form-box" onSubmit={handleSubmit}>
        <h2>PAN Card Application</h2>

        <input
          name="fullName"
          placeholder="Full Name"
          value={fields.fullName}
          onChange={handleInput}
        />
        <span className="error">{errors.fullName}</span>

        <input
          name="fatherName"
          placeholder="Father's Name"
          value={fields.fatherName}
          onChange={handleInput}
        />
        <span className="error">{errors.fatherName}</span>

        <input
          type="date"
          name="dob"
          value={fields.dob}
          onChange={handleInput}
        />
        <span className="error">{errors.dob}</span>

        <input
          name="email"
          placeholder="Email"
          value={fields.email}
          onChange={handleInput}
        />
        <span className="error">{errors.email}</span>

        <input
          name="phone"
          placeholder="Phone Number"
          value={fields.phone}
          onChange={handleInput}
        />
        <span className="error">{errors.phone}</span>

        <textarea
          name="address"
          placeholder="Full Address"
          value={fields.address}
          onChange={handleInput}
        />
        <span className="error">{errors.address}</span>

        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>

        {submitted && <p className="success-text">Form submitted successfully!</p>}
      </form>
    </div>
  );
};

export default PanCardForm;
