import React, { useState } from "react";
import { validateForm } from "./FormValidation";
import { submitJobForm } from "../api/formApi";
import "./FormStyles.css";

const JobApplyForm = () => {
  const [fields, setFields] = useState({
    fullName: "",
    email: "",
    phone: "",
    qualification: "",
    jobType: "",
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  const inputHandler = (e) => {
    setFields({ ...fields, [e.target.name]: e.target.value });
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    const validation = validateForm(fields);

    if (Object.keys(validation).length !== 0) {
      setErrors(validation);
      return;
    }

    setErrors({});
    setLoading(true);

    console.log("ðŸ“¤ Sending Job Form:", fields);
    const response = await submitJobForm(fields);
    console.log("ðŸ“¥ Backend Response:", response);

    setLoading(false);

    if (response.success) {
      setSubmitted(true);
      alert("Job application submitted!");
    } else {
      alert("Error: " + response.message);
    }
  };

  return (
    <form className="form-box" onSubmit={submitHandler}>
      <h2>Job Application Form</h2>

      <input
        type="text"
        name="fullName"
        placeholder="Full Name"
        value={fields.fullName}
        onChange={inputHandler}
      />
      <span className="error">{errors.fullName}</span>

      <input
        type="email"
        name="email"
        placeholder="Email Address"
        value={fields.email}
        onChange={inputHandler}
      />
      <span className="error">{errors.email}</span>

      <input
        type="text"
        name="phone"
        placeholder="Phone Number"
        value={fields.phone}
        onChange={inputHandler}
      />
      <span className="error">{errors.phone}</span>

      <input
        type="text"
        name="qualification"
        placeholder="Highest Qualification"
        value={fields.qualification}
        onChange={inputHandler}
      />
      <span className="error">{errors.qualification}</span>

      <input
        type="text"
        name="jobType"
        placeholder="Job Type (Govt/Private)"
        value={fields.jobType}
        onChange={inputHandler}
      />
      <span className="error">{errors.jobType}</span>

      <button type="submit">{loading ? "Submitting..." : "Submit"}</button>

      {submitted && <p className="success-text">Form submitted successfully!</p>}
    </form>
  );
};

export default JobApplyForm;
