import React, { useState } from "react";
import "./Shop.css";

const Shop = () => {
  const [category, setCategory] = useState("ALL");

  // ⭐ Dummy Amazon Products — Replace links with your affiliate links
  const products = [
    {
      id: 1,
      title: "HP Laptop 8GB RAM / 512GB SSD",
      price: "₹42,999",
      img: "https://m.media-amazon.com/images/I/71DwIcS8loL._SL1500_.jpg",
      category: "Electronics",
      link: "https://amazon.in",
    },
    {
      id: 2,
      title: "Fastrack Smart Watch",
      price: "₹1,499",
      img: "https://m.media-amazon.com/images/I/61VfL-aiToL._SL1500_.jpg",
      category: "Electronics",
      link: "https://amazon.in",
    },
    {
      id: 3,
      title: "Campus Men's Running Shoes",
      price: "₹899",
      img: "https://m.media-amazon.com/images/I/71chQkx2JUL._SL1500_.jpg",
      category: "Fashion",
      link: "https://amazon.in",
    },
    {
      id: 4,
      title: "Home Storage Rack",
      price: "₹799",
      img: "https://m.media-amazon.com/images/I/61fmG7nFJzL._SL1500_.jpg",
      category: "Home",
      link: "https://amazon.in",
    },
  ];

  const categories = ["ALL", "Electronics", "Fashion", "Home"];

  const filteredProducts =
    category === "ALL"
      ? products
      : products.filter((p) => p.category === category);

  return (
    <div className="shop-page">
      <h1 className="shop-title">Rahat Digitals Shop</h1>
      <p className="shop-sub">
        Buy Best Amazon Products Using Our Affiliate Store
      </p>

      {/* Category Filters */}
      <div className="shop-filters">
        {categories.map((cat) => (
          <button
            key={cat}
            className={category === cat ? "filter-btn active" : "filter-btn"}
            onClick={() => setCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.map((p) => (
          <div className="product-card" key={p.id}>
            <img src={p.img} alt={p.title} />

            <h3>{p.title}</h3>
            <p className="price">{p.price}</p>

            <a
              href={p.link}
              target="_blank"
              rel="noopener noreferrer"
              className="buy-btn"
            >
              Buy Now on Amazon
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Shop;
